import React, { Component, useState } from 'react';
import {
  Container,
  Row,
  Col,
  Form,
  Button,
  FormGroup,
  Input,
  FormFeedback,
  Label,
} from 'reactstrap';
import './styles.scss';
//Import Image
import img from '../../assets/images/onepage/bg.png';
import classic from '../../assets/images/saas/classic02.png';

import * as Yup from 'yup';
import { useFormik } from 'formik';

import FeatherIcon from 'feather-icons-react';

const Section = () => {
  const [succeMsg, setsucceMsg] = useState(false);

  const validation = useFormik({
    enableReinitialize: true,

    initialValues: {
      name: '',
      email: '',
    },
    validationSchema: Yup.object({
      name: Yup.string().required('Please Enter Name'),
      email: Yup.string().required('Email is required'),
    }),
    onSubmit: (values) => {
      setsucceMsg(true);
      // console.log(values)
    },
  });
  return (
    <React.Fragment>
      <section
        className="bg-home bg-light d-flex align-items-center"
        style={{
          background: `url(${img}) top left no-repeat`,
          height: 'auto',
        }}
        id="home"
      >
        <Container>
          <Row className="justify-content-center align-items-center">
            <Col className="text-center mt-0 mt-md-5 pt-0 pt-md-5">
              <div className="title-heading margin-top-100">
                <h1
                  className="heading mb-3"
                  data-aos="fade-up"
                  data-aos-duration="1000"
                >
                  <span className="text-primary">Advanced</span> controls.
                  <br />
                  <span className="text-primary">
                    Simple
                  </span> implementation.{' '}
                </h1>
                <p
                  className="para-desc mx-auto text-muted"
                  data-aos="fade-up"
                  data-aos-duration="1400"
                >
                  Skyrocket your lighting business by offering advanced controls
                  capabilities with JarvisLink.
                </p>
              </div>
            </Col>
            <Col
              lg="5"
              md={{ size: 6, order: 1 }}
              xs={{ order: 2 }}
              className="mt-4 pt-2"
            ></Col>
          </Row>
          <Row>
            <div
              className="home-dashboard onepage-hero"
              data-aos="fade-up"
              data-aos-duration="2200"
            >
              <img src={classic} alt="" className="img-fluid rounded" />
            </div>
          </Row>
        </Container>
      </section>
    </React.Fragment>
  );
};

export default Section;
