import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { ButtonToggle, Container } from 'reactstrap';
import ScrollspyNav from './scrollSpy';

//Import Images
import logodark from '../../assets/images/logo-dark.png';

import RightSidebar from '../../components/Layout/RightSidebar';

//React Drawer
import ReactDrawer from 'react-drawer';
import 'react-drawer/lib/react-drawer.css';

//Import Icons
import FeatherIcon from 'feather-icons-react';

class NavbarPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      navItems: [
        { id: 1, idnm: 'home', navheading: 'Home' },
        { id: 3, idnm: 'service', navheading: 'Features' },
        { id: 3, idnm: 'documentation', navheading: 'Documentation' },
        // { id: 4, idnm: 'team', navheading: 'Team' },
        { id: 6, idnm: 'contact', navheading: 'Contact' },
      ],
      open: false,
      position: 'right',
    };
    this.toggleLine = this.toggleLine.bind(this);
    this.toggleRightDrawer = this.toggleRightDrawer.bind(this);
    this.onDrawerClose = this.onDrawerClose.bind(this);
  }

  toggleLine() {
    this.setState((prevState) => ({ isOpen: !prevState.isOpen }));
  }
  toggleRightDrawer = () => {
    this.setState({ position: 'right' });
    this.setState({ open: !this.state.open });
  };
  onDrawerClose = () => {
    this.setState({ open: false });
  };

  initMenu() {
    this.activateMenu();
  }

  render() {
    //Store all Navigationbar Id into TargetID variable(Used for Scrollspy)
    let targetId = this.state.navItems.map((item) => {
      return item.idnm;
    });
    return (
      <React.Fragment>
        <header id="topnav" className="defaultscroll sticky">
          <Container>
            <div>
              <Link className="logo" to="#">
                <img src={logodark} height="24" alt="" />
              </Link>
            </div>
            <ul className="buy-button list-inline mb-0">
              <li className="list-inline-item button mb-0">
                <a className="btn" href="https://www.jarvislighting.com/">
                  <div className="btn btn-pills btn-outline-primary">
                    Jarvis Lighting
                  </div>
                </a>
              </li>
              <li className="list-inline-item button mb-0">
                <a
                  href="https://www.jarvislink.com/#/access/login"
                  className="btn btn-primary btn-pills"
                  // onClick={this.toggleRightDrawer}
                  // disabled={this.state.open}
                >
                  Login
                </a>
              </li>
            </ul>
            <div className="menu-extras">
              <div className="menu-item">
                <Link
                  to="#"
                  onClick={this.toggleLine}
                  className={
                    this.state.isOpen ? 'navbar-toggle open' : 'navbar-toggle'
                  }
                >
                  <div className="lines">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                </Link>
              </div>
            </div>
            <ScrollspyNav
              scrollTargetIds={targetId}
              scrollDuration="100"
              headerBackground="false"
              activeNavClass="active"
            >
              <div
                id="navigation"
                style={{ display: this.state.isOpen ? 'block' : 'none' }}
              >
                <ul className="navigation-menu">
                  {this.state.navItems.map((item, key) => (
                    <li
                      key={key}
                      className={
                        item.navheading === 'Home'
                          ? 'has-submenu active'
                          : 'has-submenu'
                      }
                    >
                      <a href={'#' + item.idnm}> {item.navheading}</a>
                    </li>
                  ))}
                </ul>
              </div>
            </ScrollspyNav>
          </Container>
        </header>
        <ReactDrawer
          open={this.state.open}
          position={this.state.position}
          onClose={this.onDrawerClose}
        >
          <RightSidebar onClose={this.onDrawerClose} />
        </ReactDrawer>
      </React.Fragment>
    );
  }
}

export default NavbarPage;
